import Vue from 'vue';
import Vuex from 'vuex';
import axios from 'axios';
import * as cookie from 'js-cookie';

Vue.use(Vuex);

const Domain = 'deepin.org';
const KeyLanguage = 'deepin_language';
const KeyUsername = 'deepinid_login';

export default new Vuex.Store({
  state: {
    language: cookie.get(KeyLanguage) || 'zh-cn',
    username: cookie.get(KeyUsername) || '',
  },
  mutations: {
    changeLanguage(state, language: string) {
      cookie.set(KeyLanguage, language, { domain: Domain });
      state.language = language;
    },
  },
  actions: {},
});
