import Vue, { PropOptions } from 'vue';
import { SearchResult } from '@/store';
export default Vue.extend({
  props: {
    name: String,
    keyworks: String,
    results: <PropOptions<SearchResult[]>>{
      default: [],
    },
  },
  data() {
    return {
      info: {
        title: '',
        icon: '',
      },
    };
  },
  methods: {
    addHighlight(raw: string) {
      return raw.replace(this.keyworks, `<span class="highlight">${this.keyworks}</span>`);
    },
  },
  mounted() {
    this.$store.dispatch('getAppInfo', this.name).then(info => {
      this.info = info;
    });
  },
});
