import marked from 'marked';
export function renderMan({ raw, path }: { raw: string; path: string }) {
  return new Promise<Result>((resolve, reject) => {
    const renderer = new marked.Renderer();

    //标题列表
    const heads = [] as Heading[];
    renderer.heading = (text, level): string => {
      if (level === 1) {
        return '';
      }
      if (text.includes('|')) {
        text = text.slice(0, text.indexOf('|'));
      }
      heads.push({ id: `h${heads.length + 1}`, level, text });
      return `<h${level} id="h${heads.length}">${text}</h${level}>`;
    };
    marked(raw, { renderer }, (err, html) => {
      if (err) {
        reject(err);
      }
      html = html.replace(/src="/g, `src="${path}/`);
      resolve({ html, heads });
    });
  });
}

export interface Result {
  html: string;
  heads: Heading[];
}
export interface Heading {
  id: string;
  text: string;
  level: number;
}
