import Vue from 'vue';
import './style.scss';
import App from './App.vue';
import router from './router';
import store from './store';

Vue.config.productionTip = false;

async function main() {
  if (window.QWebChannel) {
    const channel = await new Promise<any>(resolve => {
      new window.QWebChannel(window.qt.webChannelTransport, resolve);
    });
    window.objects = channel.objects;
  }
  new Vue({
    router,
    store,
    render: h => h(App),
  }).$mount('#app');
}

// 启动错误处理
main().catch((err: Error) => {
  document.body.style.color = 'red';
  document.body.innerText = err.message;
});

// 全局QT类型声明
declare global {
  interface Window {
    requestIdleCallback: (callback: Function) => void;
    QWebChannel: any;
    qt: {
      webChannelTransport: any;
    };

    objects: Objects;
    index: () => void;
    openApp: (appName: string, heading?: string) => void;
    openSearchPage: (app: string) => void;
  }
}

interface Objects {
  i18n: {
    getLocale: (callback: (language: string) => void) => void;
    getSentences: (callback: (sentences: { [key: string]: string }) => void) => void;
  };
  imageViewer: {
    open: (imgSrc: string) => void;
  };
  manual: {
    getSystemManualDir: (callback: (dir: string) => void) => void;
    getSystemManualList: (callback: (list: string[]) => void) => void;
    openExternalLink: (link: string) => void;
  };
  search: {
    mismatch: {
      connect: (callback: (works: string) => void) => void;
      disconnect: (callback: Function) => void;
    };
    onContentResult: {
      connect: (
        callback: (appName: string, titles: string[], heads: string[], contents: string[]) => void,
      ) => void;
      disconnect: (callback: Function) => void;
    };
  };
}
