export async function waitImg(el: HTMLElement) {
  const imgListLoad = Array.from(el.querySelectorAll<HTMLImageElement>('IMG')).map(img => {
    return new Promise(resolve => {
      img.onerror = resolve;
      img.onload = resolve;
      if (img.complete) {
        resolve();
      }
    });
  });
  await Promise.all(imgListLoad);
}
