import Vue from 'vue';
import SearchItem from '@/components/Search-item.vue';
import { SearchResult, Dict } from '@/store';
export default Vue.extend({
  components: { SearchItem },
  computed: {
    dict(): Dict {
      return this.$store.state.dict;
    },
    noResult(): string {
      return this.dict.NoResult.replace('%1', `<div class="keyworks">${this.keyworks}</div>`);
    },
    keyworks(): string {
      return this.$route.query.keyworks;
    },
    mismatch(): boolean {
      return this.$route.query.mismatch === 'mismatch';
    },
    searchResults(): { [key: string]: SearchResult[] } {
      return this.$store.state.searchResults;
    },
  },
  watch: {
    $route() {
      this.$store.commit('clearSearchResult');
    },
  },
  methods: {
    searchInWiki() {
      const link = `https://wiki.deepin.org/index.php?title&search=${encodeURIComponent(
        this.keyworks,
      )}`;
      this.$store.dispatch('openLink', link);
    },
  },
});
