import Vue from 'vue';
import MyPreviewMan from '@/components/Preview-Man.vue';
import scrollIntoView from 'smooth-scroll-into-view-if-needed';
import { PreviewMan } from '@/store';
import { waitImg } from '@/helper/wait-img';
export default Vue.extend({
  components: { MyPreviewMan },
  props: {
    html: String,
  },
  data() {
    return {
      currentHeading: '',
      headingScroll: 0,
    };
  },
  computed: {
    heading(): string {
      return this.$store.state.heading;
    },
    navWidth(): number {
      return this.$store.state.navWidth;
    },
    previewMan(): PreviewMan {
      return this.$store.state.previewMan;
    },
    content(): HTMLDivElement {
      // 路由变化 计算属性刷新
      const _ = this.$route;
      return this.$refs.content as HTMLDivElement;
    },
    hList(): Array<HTMLHeadingElement> {
      return Array.from<HTMLHeadingElement>(this.content.querySelectorAll('h2,h3'));
    },
  },
  watch: {
    heading(val: string) {
      this.scrollTo(val);
    },
    $route: {
      immediate: true,
      handler() {
        const heading = this.$route.query.heading;
        this.currentHeading = '';
        Vue.nextTick(() => {
          waitImg(this.content).then(() => {
            this.scrollTo(heading);
          });
        });
      },
    },
  },
  methods: {
    scrollTo(heading: string) {
      if (this.currentHeading === heading) {
        return;
      }
      let el = document.getElementById(heading);
      if (!el) {
        el = document.getElementById('h1');
      }
      if (el) {
        this.currentHeading = el.id;
        this.headingScroll++;
        scrollIntoView(el, { block: 'start' }).then(() => {
          this.headingScroll--;
        });
        this.$store.commit('setHeading', this.currentHeading);
      }
    },
    scroll() {
      if (this.headingScroll != 0) {
        return;
      }
      if (this.previewMan) {
        this.$store.commit('previewMan', null);
      }
      const limit = window.innerHeight / 2;
      for (const h of this.hList) {
        const top = h.getBoundingClientRect().top;
        if (top > limit) {
          break;
        }
        this.currentHeading = h.id;
        if (top >= 0 && top < limit) {
          break;
        }
      }
      if (this.currentHeading != this.heading) {
        this.$store.commit('setHeading', this.currentHeading);
      }
    },
    click(e: MouseEvent) {
      switch (e.toElement.nodeName) {
        case 'IMG': {
          const el = e.toElement as HTMLImageElement;
          const ext = el.src.slice(el.src.lastIndexOf('.') + 1);
          if (['jpg', 'png', 'jpeg'].includes(ext)) {
            this.$store.commit('previewImage', el.src);
          }
          break;
        }
        case 'A': {
          const el = e.toElement as HTMLLinkElement;
          const href = el.getAttribute('href');
          if (!href) {
            return;
          }
          switch (true) {
            // 锚跳转
            case href.startsWith('#'): {
              const hash = href.slice('#'.length);
              const el = this.hList.find(heading => heading.innerText === hash);
              if (el) {
                this.scrollTo(el.id);
              }
              break;
            }
            // 弹窗预览
            case href.startsWith('dman://'): {
              let [name, heading] = href.slice('dman://'.length).split('#');
              if (name.startsWith('/')) {
                name = name.slice(1);
              }
              if (
                this.previewMan &&
                this.previewMan.name === name &&
                this.previewMan.heading === heading
              ) {
                this.$store.commit('previewMan', null);
              } else {
                this.$store.commit('previewMan', { name, heading, x: e.clientX, y: e.clientY });
              }
              break;
            }
          }
          break;
        }
        default:
          if (this.previewMan) {
            this.$store.commit('previewMan', null);
          }
      }
    },
  },
});
